mapper.select:
select post.id,avatar,name,title,content_text,color,lyrics,like_num,collection_num
from user,post where user.id=post.user_id

    and user.id not in

        #{id}


order by post.id desc
limit #{start},#{pageSize}

select post.id,user_id,avatar,name,title,content_text,color,lyrics,like_num,collection_num from user,post
user.id=post.user_id
and user.id not in
#{id}
and color = #{color}
and (content_text like concat('%',#{text},'%') or title like concat('%',#{text},'%') or user.name like concat('%',#{text},'%'))
order by post.id desc limit #{start},#{pageSize}

select post.id,user_id,avatar,name,title,content_text,color,lyrics,like_num,collection_num
from user,post where user.id=post.user_id and user_id in

    #{userId}

order by post.id desc
limit #{start},#{pageSize}
